            newReZ = reZ * reZ - imZ * imZ + reC;
            newImZ = 2 * reZ * imZ + imC;
            reZ = newReZ;
            imZ = newImZ;

            ++iteration;

        }

        buffer[j][k] = iteration;
    }
}

