int atlagosszeg = 0, melyseg = 0, atlagdb = 0;
void
ratlag (BINFA_PTR fa)
{
  if (fa != NULL)
    {
      ++melyseg;
      ratlag (fa->jobb_egy);
      ratlag (fa->bal_nulla);
      --melyseg;
      if (fa->jobb_egy == NULL && fa->bal_nulla == NULL)
    {
      ++atlagdb;
      atlagosszeg += melyseg;
    }
    }
}
double szorasosszeg = 0.0, atlag = 0.0;
void
rszoras (BINFA_PTR fa)
{
  if (fa != NULL)
    {
      ++melyseg;
      rszoras (fa->jobb_egy);
      rszoras (fa->bal_nulla);
      --melyseg;
      if (fa->jobb_egy == NULL && fa->bal_nulla == NULL)
    {
      ++atlagdb;
      szorasosszeg += ((melyseg - atlag) * (melyseg - atlag));
    }
    }
}
int max_melyseg = 0;
void
kiir (BINFA_PTR elem)
{
  if (elem != NULL)
    {
      ++melyseg;
      if (melyseg > max_melyseg)
    max_melyseg = melyseg;
      kiir (elem->jobb_egy);
      for (int i = 0; i < melyseg; ++i)
    printf ("---");
      printf ("%c(%d)\n", elem->ertek < 2 ? '0' + elem->ertek : elem->ertek,
          melyseg-1);
      kiir (elem->bal_nulla);
      --melyseg;
    }
}
void
szabadit (BINFA_PTR elem)
{
  if (elem != NULL)
    {
      szabadit (elem->jobb_egy);
      szabadit (elem->bal_nulla);
      free (elem);
    }
}

//preorder fabejárás:
void
kiir (BINFA_PTR elem)
{
  if (elem != NULL)
    {
	printf ("%c", elem->ertek < 2 ? '0' + elem->ertek : elem->ertek);      
	kiir (elem->bal_nulla);	
	kiir (elem->jobb_egy);      
    }
}

//inorder fabejárás:
void
kiir (BINFA_PTR elem)
{
  if (elem != NULL)
    {
	kiir (elem->bal_nulla);
	printf ("%c", elem->ertek < 2 ? '0' + elem->ertek : elem->ertek);      	
	kiir (elem->jobb_egy);      
    }
}

//postorder fabejárás:            
void
kiir (BINFA_PTR elem)
{
  if (elem != NULL)
    {
	kiir (elem->bal_nulla);     	
	kiir (elem->jobb_egy);
	printf ("%c", elem->ertek < 2 ? '0' + elem->ertek : elem->ertek);       
    }
}

    class LZWBinFa
    {
    public:
    LZWBinFa (char b = '/'):betu (b), balNulla (NULL), jobbEgy (NULL) {};
    ~LZWBinFa () {};
        void operator<<(char b)
{
    if (b == '0')
    {
        // van '0'-s gyermeke az aktuális csomópontnak?
        if (!fa->nullasGyermek ()) // ha nincs, csinálunk
        {
            Csomopont *uj = new Csomopont ('0');
            fa->ujNullasGyermek (uj);
            fa = &gyoker;
        }
        else // ha van, arra lépünk
        {
            fa = fa->nullasGyermek ();
        }
    }
    else
    {
        if (!fa->egyesGyermek ())
        {
            Csomopont *uj = new Csomopont ('1');
            fa->ujEgyesGyermek (uj);
            fa = &gyoker;
        }
        else
        {
            fa = fa->egyesGyermek ();
        }
    }
}

class Csomopont
    {
    public:
        Csomopont (char b = '/'):betu (b), balNulla (0), jobbEgy (0) {};
        ~Csomopont () {};
        Csomopont *nullasGyermek () {
            return balNulla;
        }
        Csomopont *egyesGyermek ()
        {
            return jobbEgy;
        }
        void ujNullasGyermek (Csomopont * gy)
        {
            balNulla = gy;
        }
        void ujEgyesGyermek (Csomopont * gy)
        {
            jobbEgy = gy;
        }
    private:
        friend class LZWBinFa;
        char betu;
        Csomopont *balNulla;
        Csomopont *jobbEgy;
        Csomopont (const Csomopont &);
        Csomopont & operator=(const Csomopont &);
    }; 

  int main ()
  {
        char b;
        LZWBinFa binFa;
        while (std::cin >> b)
        {
        binFa << b;
        }
    binFa.kiir ();
    binFa.szabadit ();
    return 0;
    }


protected:          
Csomopont *gyoker; 
int maxMelyseg;
double atlag, szoras;

//előtte:
fa=&gyoker;
//utánna:
fa=gyoker;

//előtte:
szabadit (gyoker.egyesGyermek ());
szabadit (gyoker.nullasGyermek ());
//utánna:
szabadit (gyoker->egyesGyermek ());
szabadit (gyoker->nullasGyermek ());
    }

LZWBinFa ()
    {
    gyoker= new Csomopont('/');
    fa = gyoker;
    }
~LZWBinFa ()
    {
    szabadit (gyoker->egyesGyermek ());
    szabadit (gyoker->nullasGyermek ());
    delete(gyoker);
    }

		 LZWBinFa ( const LZWBinFa & regi ) {

       gyoker.ujEgyesGyermek ( masol ( regi.gyoker.egyesGyermek (), regi.fa ) );
       gyoker.ujNullasGyermek ( masol ( regi.gyoker.nullasGyermek (), regi.fa ) );

       if ( regi.fa == & ( regi.gyoker ) )
            fa = &gyoker;

  }

  LZWBinFa ( LZWBinFa && regi ) {

       gyoker.ujEgyesGyermek ( regi.gyoker.egyesGyermek() );
       gyoker.ujNullasGyermek ( regi.gyoker.nullasGyermek() );

       regi.gyoker.ujEgyesGyermek ( nullptr );
       regi.gyoker.ujNullasGyermek ( nullptr );

  }

  Csomopont *  masol ( Csomopont * elem, Csomopont * regifa ) {

     Csomopont * ujelem = NULL;

     if ( elem != NULL ) {
          ujelem = new Csomopont ( elem->getBetu() );

          ujelem->ujEgyesGyermek ( masol ( elem->egyesGyermek (), regifa ) );
          ujelem->ujNullasGyermek ( masol ( elem->nullasGyermek (), regifa ) );

          if ( regifa == elem )
               fa = ujelem;

     }

     return ujelem;
}
